CREATE TABLE USER1(USERID INT PRIMARY KEY, USERNAME VARCHAR(20) NOT NULL, GENDER CHAR(1), DOB DATE NOT NULL, AGE INT, CITY VARCHAR(20), STATE VARCHAR(20), PINCODE INT);
CREATE TABLE BOOKING(PNR_NO NUMERIC(10,0) PRIMARY KEY, TRAIN_NO VARCHAR(10) NOT NULL,TRAIN_NAME VARCHAR(20), USERID INT NOT NULL, USERNAME VARCHAR(20) NOT NULL, TRAVELING_DATE DATE, CLASS CHAR(2), SOURCE_PLACE VARCHAR(20), DESTINATION VARCHAR(20) NOT NULL, FARE INT);
CREATE TABLE PAYMENT(ACCOUNT_NO INT PRIMARY KEY, PNR_NO NUMERIC(10,0) NOT NULL UNIQUE, USERID INT NOT NULL, USERNAME VARCHAR(20) NOT NULL, PAYMENT_TYPE VARCHAR(10), RECEIVING VARCHAR(4), PAYMENT_DATE DATE);
CREATE TABLE TRAIN_INFO(TRAIN_NO VARCHAR(10), TRAIN_NAME VARCHAR(20), USERID INT NOT NULL, USERNAME VARCHAR(20), DEPT_NAME VARCHAR(20), DISTANCE VARCHAR(10), SEAT_AVAILABLE INT, SOURCE_PLACE VARCHAR(20),DESTINATION VARCHAR(20), DEPARTURE_DATE DATE, DEPARTURE_TIME TIME, ARRIVAL_DATE DATE, ARRIVAL_TIME TIME); 
CREATE TABLE ROUTE1 (TRAIN_NO VARCHAR(10), TRAIN_NAME VARCHAR(20), DIRECTION VARCHAR(10), STOPPAGE VARCHAR(20), SOURCE_PLACE VARCHAR(20), DESTINATION VARCHAR(20));
CREATE TABLE TICKET(PNR_NO NUMERIC(10,0) PRIMARY KEY, TRAIN_NO VARCHAR(10) NOT NULL,TRAIN_NAME VARCHAR(20), USERID INT NOT NULL, USERNAME VARCHAR(20) NOT NULL, TRAVELING_DATE DATE, CLASS CHAR(2), BERTH VARCHAR(10),SEAT_NO VARCHAR(10),SOURCE_PLACE VARCHAR(20), DESTINATION VARCHAR(20) NOT NULL, FARE INT,DEPARTURE_TIME TIME,ARRIVAL_TIME TIME);sys_config